import React from 'react';
import './App.css';
import utfsm from './utfsm.png'
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from "react-router-dom";
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

import Solicitud from './components/Solicitud.js'
import Reserva from './components/Reserva.js'
import Index from "./components/Index.js"
import Navbar from 'react-bootstrap/Navbar'
import solicitudService from './components/services/solicitudService';
import Editar from './components/Editar.js'
import Nav from 'react-bootstrap/Nav'

import 'bootstrap/dist/css/bootstrap.min.css';



class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {solicitudes:"",reservas:""};
    this.get_solicitudes = this.get_solicitudes.bind(this)
  }

  get_solicitudes(){
    var listasol = []
    var listares = []
    solicitudService.getAll().then
      (res=>{var pac=res.data;
            for (var i in pac){
            var obj = pac[i].reserva
            if (obj === true){ 
              listares.push(pac[i])
            }
            else{
              listasol.push(pac[i])
            }
          }
          this.setState({solicitudes:listasol,reservas:listares})

  })
  }



  
  render(){

  return (

<Container style = {{padding:"0",margin:"0"}} fluid>
  <Row>
    <Col style = {{width:'100%'}}>
        <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
            <Navbar.Brand href="/index">
                  <a href = "/index">
                      <img 
                        width="60"
                          height="50"
                          src ={utfsm} alt= 'icon' />
                  </a>
              </Navbar.Brand>
              <Navbar.Toggle aria-controls="responsive-navbar-nav" />
              <Navbar.Collapse id="responsive-navbar-nav">
                <Nav className="mr-auto">
                  <Nav.Link href="/Solicitud">Solicitudes</Nav.Link>
                  <Nav.Link href="/reserva">Reservas</Nav.Link>
                </Nav>
                <Nav>
                  <Nav.Link href="/paciente">Inscribir Paciente</Nav.Link>
                </Nav>
              </Navbar.Collapse>
        </Navbar>
        </Col>
        </Row>

        
<Row>
  <Col>
      <Router>
          <Switch>
                <Route path = "/dashboard">
                   <Index solicitudes = {this.state.solicitudes} reservas = {this.state.reservas}/>
                </Route>
                <Route path="/solicitud">
                    <Solicitud/>
                </Route>
                <Route path = "/dashboard/editar">
                      <Editar solicitudes = {this.state.solicitudes} />
                </Route>

                
                
                

                <Route path="/reserva">
                   <Reserva />
                </Route>
                <Redirect from =  "/" to="/dashboard" />
          </Switch>
     </Router>

    </Col>
    
  </Row>
 </Container>  

  )
  }}
export default App;
