import { api } from './helpers/api.js';

const basePath = 'api';

let config = {
    headers: {
       'Content-Type': 'application/json',
    } 
}

function getAll() { return api.get(`${basePath}/solicitudes`); }

function show(solicitudId) { return api.get(`${basePath}/?id=${solicitudId}`); }

function create(data) { return api.post(`${basePath}/solicitudes`, data,config); }

const solicitudService = { getAll, show, create };
export default solicitudService;
