import './Reserva.css';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';
import pacientesService from './services/pacientesService';
import Print from './Print.js'


import 'bootstrap/dist/css/bootstrap.min.css';
var React = require('react');

class Reserva extends React.Component{
    constructor(props){
        super(props);
        this.state = {ingresado:false,nombre: '', rut: '', TipoPaciente: '',sillon: false, pabellon:false,equipo:false,equipamiento:false,
    tipoequipamiento:'',tipoequipo:'',recuperacion:false};

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    handleChange(event){

        if (event.target.name === "nombre"){
          alert(this.state.tipoequipamiento)
            this.setState({nombre:event.target.value});
        }
        else if (event.target.name === "rut"){
            this.setState({rut:event.target.value})
        }
        else if (event.target.name ==="equipo"){
            this.setState({equipo:!this.state.equipo})
        }
        else if (event.target.name ==="sillon"){
            this.setState({sillon:!this.state.sillon})
        }
        else if (event.target.name ==="pabellon"){
            this.setState({pabellon:!this.state.pabellon})
        }
        else if (event.target.name ==="equipamiento"){
            this.setState({equipamiento:!this.state.equipamiento})
        }
        else if (event.target.name ==="recuperacion"){
            this.setState({recuperacion:!this.state.recuperacion})
        }
        
        else if (event.target.name ==="tipoequipo"){
            alert(event.target.name)
            this.setState({tipoequipo:event.target.value})
        }
        else if (event.target.name ==="tipoequipamiento"){
            alert(event.target.name)
            this.setState({tipoequipamiento:event.target.value})
        }


    }

    handleClick(event){
        setTimeout(function() {

        }, 1000);
       
    }
    handleSubmit(event){
     
      event.preventDefault();
       this.setState({ingresado:true})
      
       pacientesService.create({rut:this.state.rut,
       nombre:this.state.nombre ,
       nacionalidad:this.state.TipoPaciente ,
       sexo:this.state.pabellon ,
      fecha_na:this.state.equipo ,
     domicilio:this.state.nombre ,
     diagnostico:this.state.tipoequipamiento ,
     telefono:this.state.tipoequipo ,
     gravedad:this.state.recuperacion
    }).then(res => {
        console.log(res);
        console.log(res.data);
      })
       

};


  /* nombre: this.state.name, 
         rut:this.state.rut, 
         TipoPaciente:this.state.TipoPaciente,
         sillon: this.state.sillon,
          pabellon:this.state.pabellon,
          equipo:this.state.equipo,
          equipamiento:this.state.equipamiento,
    tipoequipamiento:this.state.tipoequipamiento, 
    tipoequipo:this.state.tipoequipo,
    recuperacion:this.state.tiporecuperacion 
       }*/

     
    render(){
        return(
            <Container >
            <Row>
           
            <Col>
            <Alert  show= {this.state.ingresado} variant="success">
        <Alert.Heading>Ingreso satisfactorio</Alert.Heading>
        <p>
          Ingresado con éxito!
        </p>
        <hr />
        <div className="d-flex justify-content-end">
          <Button onClick={this.handleClick} variant="outline-success">
            Cerrar
          </Button>
        </div>
      </Alert>

            <Form  onSubmit = {this.handleSubmit}>
  <Form.Group controlId="formNombre">
    <Form.Label>Nombre Paciente</Form.Label>
    <Form.Control name = "nombre" type="text" placeholder="Ingrese Nombre" onChange={this.handleChange}/>
  </Form.Group>

  <Form.Group controlId="formRut">
    <Form.Label>Rut</Form.Label>
    <Form.Control name ="rut" onChange={this.handleChange} type="text" placeholder="Rut" />
  </Form.Group>
  <Form.Group controlId="formSillon">
    <Form.Check name = "sillon" type="checkbox" label="Sillón" onChange={this.handleChange}/>
  </Form.Group>
  <Form.Group controlId="formPabellon">
    <Form.Check name = "pabellon" type="checkbox" label="Pabellón" onChange={this.handleChange}/>
  </Form.Group>
  <Form.Group controlId="formRecuperacion">
    <Form.Check name = "recuperacion" type="checkbox" label="Sala de Recuperación" onChange={this.handleChange}/>
  </Form.Group>
  <Form.Group controlId="formEquipo">
    <Form.Check name = "equipo" type="checkbox" label="Equipo" onChange={this.handleChange}/>
  </Form.Group>

  <Form.Group name = "tipoequipo" onClick={this.handleChange} hidden ={(this.state.equipo)? "":"hidden"} controlId="formTipoEquipo">
    <Form.Label>Tipo de equipo</Form.Label>
    <Form.Control  as="select" custom>
      <option>Cirujano</option>
      <option>Enfermas</option>
      <option>Urgencias</option>
    </Form.Control>
  </Form.Group>

  <Form.Group controlId="formEquipamiento">
    <Form.Check name = "equipamiento" type="checkbox" label="Equipamiento" onChange={this.handleChange}/>
  </Form.Group>

  <Form.Group name = "tipoequipamiento" onChange={this.handleChange}  hidden ={(this.state.equipamiento)? "":"hidden"} controlId="formTipoEquipamiento">
    <Form.Label>Tipo de equipamiento</Form.Label>
    <Form.Control   as="select" custom>
      <option>Bisturí</option>
      <option>Tijeras</option>
      <option>Termómetro</option>
    </Form.Control>
  </Form.Group>



  <Button  onClick = {this.handleClick}  variant="primary" type="submit">
    Submit
  </Button>
</Form>


</Col>
<Print sillon = {this.state.sillon}
        pabellon = {this.state.pabellon}
        recuperacion = {this.state.recuperacion} 
         equipo = {this.state.equipo} 
         equipamiento = {this.state.equipamiento}  
        ingresado = {this.state.ingresado} 
        nombre= {this.state.nombre} 
        rut = {this.state.rut} 
        tipoequipamiento = {this.state.tipoequipamiento}
        tipoequipo = {this.state.tipoequipo}/>
      
            </Row>
            </Container>
            

       
        );
    }
};

export default Reserva
