import './Reserva.css'
var React = require('react');

class Reserva extends React.Component{
    constructor(props){
        super(props);
        this.state = {nombre: 'Invitado', rut: 'Ingrese Rut', TipoPaciente: '',sillon: false, pabellon:false,equipo:false,equipamiento:false,
    tipoequipamiento:'',tipoequipo:''};

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event){

        if (event.target.name === "nombre"){
            this.setState({nombre:event.target.value});
        }
        else if (event.target.name === "rut"){
            this.setState({rut:event.target.value})
        }
        else if (event.target.name ==="equipo"){
            this.setState({equipo:!this.state.equipo})
        }
        else if (event.target.name ==="sillon"){
            this.setState({equipo:!this.state.sillon})
        }
        else if (event.target.name ==="pabellon"){
            this.setState({equipo:!this.state.pabellon})
        }
        else if (event.target.name ==="equipamiento"){
            this.setState({equipo:!this.state.equipamiento})
        }
        
        
        else if (event.target.name ==="tipoequipo"){
            this.setState({tipoequipo:event.target.value})
        }
        else if (event.target.name ==="tipoequipamiento"){
            this.setState({tipoequipamiento:event.target.value})
        }


    }
    handleSubmit(event){
        alert("Ingresado paciente: "+this.state.nombre+"\n"+ "Rut: " +this.state.rut);
        event.preventDefault()

    }
     
    render(){
        return(
            <div className = "reserva" >
            hola {this.state.nombre}
            <h1 style = {{fontWeight : "bold"}}> Reserva</h1>
            <form onSubmit = {this.handleSubmit}>
                <label>
                    Nombre Paciente: <br />
                    <input type="text" name = "nombre" value={this.state.nombre} onChange={this.handleChange} />
                </label>
                <br />
                <label>
                     Rut Paciente:  <br />
                    <input type="text" name = "rut" value={this.state.rut}  onChange={this.handleChange} />

                </label>
                <br />
                <label>
                    Sillón<br />
                    <input type="checkbox" name = "sillon" checked = {this.state.sillon} onChange = {this.handleChange} /><br />
                </label>
                <br />
                <label>
                    Pabellón<br />
                    <input type="checkbox" name = "Pabellon" checked = {this.state.pabellon} onChange = {this.handleChange} /><br />
                </label>
                <br />
                
                <label>
                    Equipo<br />
                    <input type="checkbox" name = "equipo" checked = {this.state.equipo} onChange = {this.handleChange} /><br />
                </label>
                <label>
                <select name = "tipoequipo" value={this.state.tipoequipo} hidden ={(this.state.equipo)? "":"hidden"} onChange={this.handleChange}>
                    <option value="Cirujanos">Cirujanos</option>
                    <option value="Enfermeras">Enfermeras</option>
                    <option value="Urgencias">Urgencias</option>
                </select>
                </label>
                <br />
                <label>
                    Equipamiento<br />
                    <input type="checkbox" name = "equipamiento" checked = {this.state.equipamiento} onChange = {this.handleChange} /><br />
                </label>
                <label>
                <select name = "tipoequipamiento" value={this.state.tipoequipamiento} hidden ={(this.state.equipamiento)? "":"hidden"} onChange={this.handleChange}>
                    <option value="Estetoscopio">Estetoscopio</option>
                    <option value="Bisturi">Bisturí</option>
                    <option value="termometro">Termómetro</option>
                </select>
                </label>
                <br />
                <input type="submit" value ="Submit" onSubmit = {this.handleSubmit} />
                



            </form>
        </div>
        );
    }
};

export default Reserva
