import './Solicitud.css';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';
import solicitudService from './services/solicitudService';
import Spinner from 'react-bootstrap/Spinner'
import {
  NavLink
} from "react-router-dom";


import 'bootstrap/dist/css/bootstrap.min.css';
var React = require('react');

class Solicitud extends React.Component{
    constructor(props){
        super(props);
        this.state = {ingresado:false, rut: '',sillon: false, pabellon:false,equipo:false,equipamiento:false,
    tipoequipamiento:'',tipoequipo:'',tiposillon:'',tipopabellon:'',recuperacion:false,hora:'',duracion:0,dia:"",carga:"",
    listarut: []};

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.obtener_lista = this.obtener_lista.bind(this);
        this.crear_lista = this.crear_lista.bind(this)
    }



    obtener_lista(){
      solicitudService.getAll()
      .then(res=>{this.res = res.data;
        var lista = res.data;
        this.setState({carga:lista})
      })
    }


     crear_lista(){
      var lista = []
            for (const i in this.state.carga){
              var obj = this.state.carga[i]
              lista.push(obj.paciente.rut)
            }
      return(lista)
  
  
    }

    handleChange(event){

      
        if (event.target.name === "rut"){
            this.setState({rut:event.target.value})
        }
        else if (event.target.name ==="equipo"){
            this.setState({equipo:!this.state.equipo})
        }
        else if (event.target.name ==="sillon"){
            this.setState({sillon:!this.state.sillon})
        }
        else if (event.target.name ==="pabellon"){
            this.setState({pabellon:!this.state.pabellon})
        }
        else if (event.target.name ==="equipamiento"){
            this.setState({equipamiento:!this.state.equipamiento})
        }
        else if (event.target.name ==="recuperacion"){
            this.setState({recuperacion:!this.state.recuperacion})
        }
        
        else if (event.target.name ==="tipoequipo"){
            this.setState({tipoequipo:event.target.value})
        }
        else if (event.target.name ==="tiposillon"){
          this.setState({tiposillon:event.target.value})
      }
      else if (event.target.name ==="tipopabellon"){
        this.setState({tipopabellon:event.target.value})
    }
    else if (event.target.name ==="hora"){
      this.setState({hora:event.target.value})
  }
  else if (event.target.name ==="dia"){
    this.setState({dia:event.target.value})
}
else if (event.target.name ==="duracion"){
  this.setState({duracion:event.target.value})
}
        else if (event.target.name ==="tipoequipamiento"){
            this.setState({tipoequipamiento:event.target.value})
        }


    }

    handleClick(event){
      this.setState({ingresado:false})
    }
    handleSubmit(event){
      
       if (this.state.listarut === ""){
       this.crear_lista()
     }
     this.obtener_lista()
    
    console.log("aa",this.state.listarut)
    if((this.state.pabellon === true) && ( this.state.tipopabellon === "" || this.state.tipopabellon ==="Elija opción" )){
      alert("Elija tipo de pabellón e intente denuevo")
     }
    else if((this.state.sillon === true) && ( this.state.tiposillon === ""  || this.state.tiposillon === "Elija opción")){
      alert("Elija tipo de sillón e intente denuevo")
     }
     else if((this.state.equipamiento === true) && ( this.state.tipoequipamiento === "" || this.state.tipoequipamiento === "Elija opción" )){
      alert("Elija tipo de equipamiento e intente denuevo")
     }
     else if((this.state.equipo === true) && ( this.state.tipoequipo === ""|| this.state.tipoequipo === "Elija opción")){
      alert("Elija tipo de equipo e intente denuevo")
     }
     else if(this.state.rut === ""){
       alert("Ingrese Rut")
     }
     else if(this.state.duracion === 0){
       alert("Ingrese Duración")
     }
     else if (this.state.dia === ""){
       alert("Ingrese Día")
     }
     else if (this.state.hora === ""){
       alert("Ingrese Hora")
     } 
     else{
       this.setState({ingresado:true})
      
       solicitudService.create({
        paciente:{rut:this.state.rut}, 
         sillon: this.state.sillon,
         pabellon:this.state.pabellon,
         equipo:this.state.equipo,
         equipamiento:this.state.equipamiento,
   tipoEquipamiento:this.state.tipoequipamiento, 
   tipoEquipo:this.state.tipoequipo,
   recuperacion:this.state.recuperacion, 
   tipoSillon:this.state.tiposillon,
   tipoPabellon:this.state.tipopabellon,
   hora:this.state.hora,
   dia:this.state.dia,
   duracion:this.state.duracion,
   reserva:0
    
    }).then(res => {
        console.log(res);
        console.log(res.data);
      }).catch(function (error) {
        console.log(error.response.data.message)})
      this.setState({ rut: '',sillon: false, pabellon:false,equipo:false,equipamiento:false,
      tipoequipamiento:'',tipoequipo:'',tiposillon:'',tipopabellon:'',recuperacion:false,hora:'',duracion:0,dia:""})
       
    }
    event.preventDefault();
};

  
  
     

     
    render(){
      if (this.state.carga === ""){
        this.obtener_lista()
      }
      if (this.state.carga[0] === undefined || this.state.carga.length !== 0){
        return (  <Spinner animation="border" variant="success" />)
      }
      else{
        return(
        
            <Container >
              <div  className = "linkres" >
          <NavLink  to="/Reserva" activeClassName="hurray">
            Administrar Reservas
          </NavLink>
          </div>
            <Row>
           
            <Col>
            <Alert  show= {this.state.ingresado} variant="success">
        <Alert.Heading>Ingreso satisfactorio</Alert.Heading>
        <p>
          Ingresado con éxito!
        </p>
        <hr />
        <div className="d-flex justify-content-end">
          <Button onClick={this.handleClick} variant="outline-success">
            Cerrar
          </Button>
        </div>
      </Alert>

            <Form  onSubmit = {this.handleSubmit}>
<Form.Row>
  <Form.Group as = {Col}  md = "6" controlId="formRut">
    <Form.Label>Rut</Form.Label>
    <Form.Control  name ="rut" onChange={this.handleChange} type="text" placeholder="Rut" />
  </Form.Group>
  </Form.Row>
  <Form.Row>
  <Form.Group as = {Col} md = "3" controlId="formHora">
    <Form.Label>Día</Form.Label>
    <Form.Control  name ="dia" onChange={this.handleChange} type="date" placeholder="dia" />
  </Form.Group>
  <Form.Group as = {Col} md = "3" controlId="formHora">
    <Form.Label>Hora</Form.Label>
    <Form.Control  name ="hora" onChange={this.handleChange} type="time" placeholder="hora" />
  </Form.Group>
  </Form.Row>
  <Form.Row>
  <Form.Group as = {Col} md = "3" controlId="formSillon">
    <Form.Check name = "sillon" type="checkbox" label="Sillón" onChange={this.handleChange}/>
  </Form.Group>
  <Form.Group as = {Col} md = "3" controlId="formDuracion">
    <Form.Label>Duración</Form.Label>
    <Form.Control  name ="duracion" onChange={this.handleChange} type="Integer" placeholder="duracion" />
  </Form.Group>
  </Form.Row>
  <Form.Group as = {Col} md = "3" hidden ={(this.state.sillon)? "":"hidden"} controlId="formTipoSillon">
    <Form.Label>Tipo de sillon</Form.Label>
    <Form.Control name = "tiposillon" onChange={this.handleChange}  as="select" custom>
    <option>Elija opción</option>
      <option>1</option>
      <option>2</option>
      <option>3</option>
    </Form.Control>
  </Form.Group>

  <Form.Group controlId="formPabellon">
    <Form.Check name = "pabellon" type="checkbox" label="Pabellón" onChange={this.handleChange}/>
  </Form.Group>
  <Form.Group as = {Col} md = "6" hidden ={(this.state.pabellon)? "":"hidden"} controlId="formTipoPabellon">
    <Form.Label>Tipo de pabellon</Form.Label>
    <Form.Control name = "tipopabellon" onChange={this.handleChange}  as="select" custom>
    <option>Elija opción</option>
      <option>1</option>
      <option>2</option>
      <option>3</option>
    </Form.Control>
  </Form.Group>
  <Form.Group controlId="formRecuperacion">
    <Form.Check name = "recuperacion" type="checkbox" label="Sala de Recuperación" onChange={this.handleChange}/>
  </Form.Group>
  <Form.Group controlId="formEquipo">
    <Form.Check name = "equipo" type="checkbox" label="Equipo" onChange={this.handleChange}/>
  </Form.Group>

  <Form.Group as = {Col} md = "6" hidden ={(this.state.equipo)? "":"hidden"} controlId="formTipoEquipo">
    <Form.Label>Tipo de equipo {this.state.tipoequipo}</Form.Label>
    <Form.Control name = "tipoequipo" onChange={this.handleChange}  as="select" custom>
     <option>Elija opción</option>
      <option>Cirujano</option>
      <option>Enfermas</option>
      <option>Urgencias</option>
    </Form.Control>
  </Form.Group>

  <Form.Group controlId="formEquipamiento">
    <Form.Check name = "equipamiento" type="checkbox" label="Equipamiento" onChange={this.handleChange}/>
  </Form.Group>

  <Form.Group as = {Col} md = "6"  hidden ={(this.state.equipamiento)? "":"hidden"} controlId="formTipoEquipamiento">
    <Form.Label>Tipo de equipamiento</Form.Label>
    <Form.Control  name = "tipoequipamiento" onChange={this.handleChange} as="select" custom>
    <option>Elija opción</option>
      <option>Bisturí</option>
      <option>Tijeras</option>
      <option>Termómetro</option>
    </Form.Control>
  </Form.Group>



  <Button  onClick = {this.handleClick}  variant="primary" type="submit">
    Submit
  </Button>
</Form>
</Col>
      
            </Row>
            </Container>
            

       
        );
    }
}};

export default Solicitud
