import ListItem from "./ListItem.js"
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button';

var React = require('react');

class List extends React.Component{


render(){
    var res = this.props.res;


    const lista = this.props.ItemList;
    if (parseInt(res) === 0){
    var todoItems = lista.map((obj) =>

  <tr key={obj.id}>
    <td>{obj.fecha}</td>
    <td> {obj.hora}</td>
   <td> {obj.equipo? "Sí":"No"}</td>
    <td>{obj.equipamiento? "Sí":"No"}</td>
    <td> {obj.paciente.rut} </td>
    <td><Button  variant="primary">Aprobar</Button> {''}
    <Button  variant="primary">Editar</Button>{' '}
    <Button  variant="danger">Rechazar Solicitud</Button></td>
  </tr>);
    }
    else if (parseInt(res) === 1){
        var todoItems = lista.map((obj) =>

  <tr key={obj.id}>
    <td>{obj.fecha}</td>
    <td> {obj.hora}</td>
   <td> {obj.equipo? "Sí":"No"}</td>
    <td>{obj.equipamiento? "Sí":"No"}</td>
    <td> {obj.sillon?"Sí":"No"}</td>
    <td> {obj.paciente.rut} </td>
    <td><Button  variant="primary">Terminar Procedimiento</Button> {''}
    <Button  variant="danger">Cancelar Reserva</Button></td>
  </tr>);
    }


    console.log(this.props.res)
    
    return (
        <Table striped bordered hover>
        <thead>
    <tr>
      <th>Fecha</th>
      <th>Hora</th>
      <th>Equipo</th>
      <th>Equipamiento</th>
      <th>Sillón</th>
      <th>Rut</th>
      <th>Acciones</th>
    </tr>
  </thead>
  <tbody>
  {todoItems}
</tbody>
</Table>
      );
    


}
}
export default List
